    public static String listarNumeros(String numerosTexto) {
        StringBuilder retorno = new StringBuilder();
        List<String> numeros = List.of(numerosTexto.split(","));

        for (String n : numeros) {
            retorno.append(n).append("\n");
        }

        return retorno.toString();
    }

    public static String listarNumerosFormatados(String numerosTexto) {
        StringBuilder retorno = new StringBuilder();
        List<String> numeros = List.of(numerosTexto.split(","));

        for (String n : numeros) {
            n = n.replaceAll("[^0-9]", "");
            if (!n.startsWith("55")) n = "55" + n;
            if (n.length() == 12) n = n.substring(0, 4) + "9" + n.substring(4);
            if (n.length() == 13) retorno.append(n).append("\n");
        }

        return retorno.toString().trim();
    }
